import { Fragment } from "react";
import { useStats } from "../hooks/useStats";
import { BASE_URL } from "../utils/constants";

export default function Stats(props) {
  const stats = useStats(`${BASE_URL}/${props.uri}`);
  if (!stats) return <p>Loading Stats...</p>;

  if (!stats.confirmed) return <p>No statistics exist.</p>;

  return (
    <Fragment>
      <section class="card">
        <div class="card-body">
          <h3 className="card-title">Confirmed</h3>
          <span className="card-text">{stats.confirmed.value}</span>
        </div>
      </section>
      <section class="card">
        <div class="card-body">
          <h3 className="card-title">Deaths</h3>
          <span className="card-text">{stats.deaths.value}</span>
        </div>
      </section>
      <section class="card">
        <div class="card-body">
          <h3 className="card-title">Recovered</h3>
          <span className="card-text">{stats.recovered.value}</span>
        </div>
      </section>
    </Fragment>
  );
}
