import { useEffect, useState } from "react";

export const useStats = function () {
  const [stats, setStats] = useState([]);

  useEffect(() => {
    async function fetchData() {
      console.log("Fetching Data");
      const data = fetch("https://covid19.mathdro.id/api").then(data => data.json());
      setStats(data);
    }

    fetchData();
  }, []);
}
